import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

// Styling
import "../styles.scss";

const ShowLink = (show: any) => (
  <li>
    <Link href="/p/[id]" as={`/p/${show.id}`}>
      <a>{show.name}</a>
    </Link>
  </li>
);

const Index = (props: any) => (
  <Layout stylingClass={"home"}>
    <div id="text-1">Hi,</div>
    <div id="text-2">I am a <span className="highlight">&nbsp;front-end developer.&nbsp;</span></div>
    <div id="text-3">
        <span id="textStatic">I can </span>
        <ul id="textRandom">
            <li>code in JavaScript.</li>
            <li>code apps with Angular.</li>
            <li>develop using Laravel PHP.</li>
            <li>write AWS Lambda functions with Node JS.</li>
            <li>have a go at React Native.</li>
            <li>experiment with React.</li>
            <li>maintain Angular JS apps.</li>
            <li>develop Wordpress' websites.</li>
            <li>code in HTML5 & CSS3.</li>
            <li>build HTML5 ad banners.</li>
            <li>code with SASS & LESS.</li>
            <li>animate with CSS3.</li>
        </ul>
    </div>
    <div className="separator"></div>
    <Link href="/portfolio">
        <a className="button">View portfolio</a>
    </Link>
    <style jsx global>{`
    .home {
        background: transparent url("../static/img/bg-home.png") no-repeat center right scroll;
        background-size: contain;
    }
    .home [id^="text-"] {
        color: #000;
        font-family: "Century Gothic", "Apple Gothic", Verdana, Arial, sans-serif;
        font-size: 32px;
        line-height: 40px;
        margin-bottom: 10px;
        position: relative;
    }
    @media screen and (max-width: 550px) {
        .home [id^="text-"] {
            font-size: 18px;
        }
    }
    .home #text-1 {
        margin: 12% 0 0;
    }
    .home #text-3 {
        height: 40px;
        overflow: hidden;
        box-sizing: content-box;
    }
    .separator {
        -webkit-animation: sepGrow 1s ease-in-out 1s forwards;
        -moz-animation: sepGrow 1s ease-in-out 1s forwards;
        -ms-animation: sepGrow 1s ease-in-out 1s forwards;
        -o-animation: sepGrow 1s ease-in-out 1s forwards;
        animation: sepGrow 1s ease-in-out 1s forwards;
        background-color: #ac2f20;
        width: 0px;
        height: 8px;
    }
    @media screen and (max-width: 550px) {
        .separator {
            width: 76px;
        }
    }
    .highlight {
        background-color: rgba(254, 154, 97, 0.85);
        color: #FFF;
    }
    #textStatic {
        display: inline-block;
        font-family: "Century Gothic", "Apple Gothic", Verdana, Arial, sans-serif;
        height: 32px;
        padding: 5px 0;
        position: absolute;
        top: 0;
    }
    #textRandom {
        background-color: transparent;
        display: inline-block;
        margin-top: 5px;
        padding: 0;
        position: absolute;
        top: 0;
        left: 92px;
        -webkit-transition: top 1s;
        -moz-transition: top 1s;
        -ms-transition: top 1s;
        -o-transition: top 1s;
        transition: top 1s;
    }
    @media screen and (max-width: 550px) {
        #textRandom {
            background-color: rgba(255, 255, 255, 0.75);
            left: 52px;
        }
    }
    #textRandom li {
        display: block;
        font-family: "Century Gothic", "Apple Gothic", Verdana, Arial, sans-serif;
        height: 32px;
        padding: 0;
        white-space: nowrap;
    }
    `}</style>
  </Layout>

);

Index.getInitialProps = async function() {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    shows: data.map((entry: any) => entry.show)
  };
};

export default Index;
